mapper.update:
update user
nick_name = #{nickName}, avatar_url = #{avatarUrl}, gender = #{gender}, name = #{name}, phone = #{phone}, sex = #{sex}, id_number = #{idNumber}, avatar = #{avatar}
where id = #{id}

mapper.select:
select count(id) from user


        and create_time >= #{begin}


        and create_time <= #{end}
